queries.query:
query @single {
  label(_:"AIDS")
  possibleDrug {
    drug: label @single
    precaution
  }
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX diseasome: <http://www4.wiwiss.fu-berlin.de/diseasome/resource/diseasome/>
PREFIX dailymed: <http://www4.wiwiss.fu-berlin.de/dailymed/resource/dailymed/>

SELECT ?possibleDrug_drug ?possibleDrug_precaution WHERE {
  ?b1 rdfs:label "AIDS"^^xsd:string;
    diseasome:possibleDrug ?possibleDrug.
  ?possibleDrug rdfs:label ?possibleDrug_drug;
    dailymed:precaution ?possibleDrug_precaution.
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX diseasome: <http://www4.wiwiss.fu-berlin.de/diseasome/resource/diseasome/>

SELECT ?treats_disease WHERE {
  ?b1 rdfs:label"Decadron (Tablet)"^^xsd:string.
  ?treats diseasome:possibleDrug ?b1;
    rdfs:label ?treats_disease.
}

query {
  trial: label @single
  trialTitle: briefTitle @single
  discussesDrug {
    drug: label @single
  }
  discussesDiseases {
    label(_:"AIDS")
  }
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX fr: <http://www.franz.com/>

SELECT ?trial ?trialTitle ?discussesDrug_drug WHERE {
  ?b1 rdfs:label ?trial;
    fr:briefTitle ?trialTitle;
    fr:discusses-drug ?discussesDrug.
  ?discussesDrug rdfs:label ?discussesDrug_drug.
  ?b1 fr:discusses-diseases ?discussesDiseases.
  ?discussesDiseases rdfs:label "AIDS"^^xsd:string.
}

SELECT DISTINCT ?subject ?label WHERE {
  { ?subject dc:title ?label }
  UNION
  { ?subject rdfs:label ?label }
}
LIMIT 100
